package test.ThinkinJava.cha5;

public class E16_StringArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String[] sa = new String[4];
		sa[0] = "These ";
		sa[1] = "are ";
		sa[2] = "some ";
		sa[3] = "strings.";
		
		for (int i = 0; i < sa.length; i++) {
			System.out.print(sa[i]);
		}
	}

}
